krpano.action:
copy(drag_currenty, y);
copy(drag_stagey, mouse.stagey);
calc(y_min, layer[get(parent)].pixelheight - pixelheight);
if(y_min LT 0,
	asyncloop(pressed,
		calc(y, drag_currenty + (mouse.stagey - drag_stagey));
		clamp(y, get(y_min), 0);
	  );
  );

if(startscene === null OR !scene[get(startscene)], copy(startscene,scene[0].name); );
loadscene(get(startscene), null, MERGE);
if(startactions !== null, startactions() );

		set(layer[map1].onclick, closemap1(); );
		

		set(bigscale,1);
		
		set(layer[map1].zorder, 2);
		tween(layer[map1].x, 0);
		tween(layer[map1].y, 0);
		tween(layer[map1].scale, get(bigscale));

		set(layer[map2].onclick, closemap2(); );
		

		set(bigscale,1);
		
		set(layer[map2].zorder, 2);
		tween(layer[map2].x, 0);
		tween(layer[map2].y, 0);
		tween(layer[map2].scale, get(bigscale));

		set(layer[map3].onclick, closemap3(); );
		

		set(bigscale,1);
		
		set(layer[map3].zorder, 2);
